mapper.select:
select ai.content_id content_id, content_type_id, title, overview, addr1 addr, first_image image, latitude, longitude
from attraction_info ai, attraction_description ad, route_attraction ra
where ai.content_id = ad.content_id and ai.content_id = ra.attraction_seq and ra.route_seq = #{ routeSeq };

select  count(ai.content_id)

from    attraction_info ai, attraction_description ad

where   ai.content_id = ad.content_id
  and   ai.sido_code = #{ sidocode }
  and   ai.gugun_code = #{ guguncode }
  and   ai.title like CONCAT('%', #{ keyword }, '%')

select ai.content_id content_id, content_type_id, title, overview, addr1 addr, first_image image, latitude, longitude
from attraction_info ai, attraction_description ad, ( select distinct sido_code, gugun_code from route_attraction ra, attraction_info ai2 where ra.route_seq = #{ routeSeq } and ra.attraction_seq = ai2.content_id ) area
where ai.content_id = ad.content_id and ai.sido_code = area.sido_code and ai.gugun_code = area.gugun_code and ai.content_type_id = #{ contentType }
limit #{ offset }, #{ limit };
