mapper.select:
select  seq, owner, name, overview, thumbnail, start_date
from    route
where   owner = #{owner};

select  seq, owner, name, overview, thumbnail, start_date
from    route
where   seq = #{seq};

mapper.update:
update route
   set name = #{name},
       overview = #{overview},
       thumbnail = #{thumbnail},
       start_date = #{startDate}
where  seq = #{seq}
  and  owner = #{owner};

mapper.delete:
delete
  from  route_attraction
 where  route_seq = #{ routeSeq }
   and  attraction_seq = #{ attractionSeq };

mapper.update:
update  route_attraction
   set  order_number = #{ order }
 where  route_seq = #{ routeSeq }
   and  attraction_seq = #{ attractionSeq };
